object SahabatData {
    private val sahabatNames = arrayOf("Abdurrahman bin 'Auf",
    "Abu Bakar Ash-Shiddiq",
    "Abu 'Ubaidah bin al-Jarrah",
    "'Ali bin Abi Thalib",
    "Sa'ad bin Abi Waqqash",
