    val listData: ArrayList<Sahabat>
        get() {
            val list = arrayListOf<Sahabat>()
            for (position in sahabatNames.indices) {
                val sahabat = Sahabat()
                sahabat.name = sahabatNames[position]
                sahabat.detail = sahabatDetails[position]
                sahabat.image = sahabatImages[position]
                sahabat.kisah = sahabatKisah[position]
                sahabat.source = sahabatSource[position]
                list.add(sahabat)
            }
            return list
        }
}